import {
  LeakAddRounded,
  LockOpenOutlined,
  LockOutlined,
} from "@mui/icons-material";
import { Box, Typography } from "@mui/material";
import React, { useContext } from "react";
import { DataBorneContext } from "./ContextBorne";
import Validation from "./Validation";

function PostLogin() {
  const {
    MyBox,
    dataBorne,
    setDataBorne,
    progressBar,
    setprogressBar,
    loginEtat,
    setLoginEtat,
    dataMKR,
  } = useContext(DataBorneContext);

  function etatBorneIcon() {
    function LockClose() {
      if (!dataMKR.isU) {
        return <LockOutlined color="green" />;
      }
      if (dataMKR.isU) {
        return <LockOutlined color="gray" />;
      }
    }
    function LockOpen() {
      if (dataMKR.isU) {
        return <LockOpenOutlined color="green" />;
      }
      if (!dataMKR.isU) {
        return <LockOpenOutlined color="gray" />;
      }
    }
    function LeakAdd() {
      if (dataMKR.isButton) {
        return <LeakAddRounded color="green" />;
      }
      if (!dataMKR.isButton) {
        return <LeakAddRounded color="gray" />;
      }
    }
    return (
      <Box
        sx={{
          display: "flex",
          flexDirection: "row",
          marginTop: 3,
          boxShadow: 5,
          borderRadius: 3,
          borderBlockColor: "gray",
          flexWrap: "wrap",
          justifyContent: "space-around",
          borderBlockWidth: 2,
          textAlign: "center",
          alignContent: "center",
          alignItems: "center",
        }}
      >
        <Box sx={{ m: 2 }}>{LockClose()}</Box>
        <Box sx={{ m: 2 }}>{LockOpen()}</Box>
        <Box sx={{ m: 2 }}>{LeakAdd()}</Box>
      </Box>
    );
  }

  return (
    <MyBox
      sx={{
        p: 2,
        m: 5,
        mx: { xs: 5, md: "25%" },
        boxShadow: 5,
        borderRadius: 3,
        borderBlockColor: "gray",
        borderBlockWidth: 2,
        textAlign: "center",
        alignContent: "center",
        alignItems: "center",
      }}
    >
      <Typography variant="h6" sx={{ p: 2, pb: 5, m: 0 }}>
        Appuyer sur le bouton pour vous connecter
      </Typography>
      <Validation />
      <Box>{etatBorneIcon()}</Box>
    </MyBox>
  );
}

export default PostLogin;
